(21, 'দিবানিশি', NULL, '<p>মারফতি নূরে উদ্ভাসিত অলক্ষে বহমান এক জনপদের কাহিনি এ উপন্যাসে বিবৃত হয়েছে সহজিয়া জীবনদর্শনে। আছে শিকড়ে ফিরতে চাওয়া ও জলের গর্ভে সব হারানাে দুই নারীর অদম্য লড়াই। এ গল্প মনসা দেবীর অভিশাপের চমকপ্রদ এক লােকজ মিথেরও। কালচক্র&rsquo;র পর আবদুল্লাহ আল ইমরান আবারাে তাঁর মােহময় লেখনীতে তুলে আনলেন চিরায়ত বাংলার প্রত্যন্ত এক জনপদের দিন-রাতের গল্প।&nbsp;</p>', 'dibanishi', '17.jpg', 'dibanishi', '478966', 'in_stock', NULL, '300.00', '225.00', 0, '100', '5', NULL, NULL, 1, 1, NULL, 'a:2:{s:5:\"front\";a:1:{i:0;N;}s:4:\"back\";a:1:{i:0;N;}}', '7', NULL, NULL, 8, '9789849277576', '3rd Edition, 2018', '206', 19, 'বাংলা', 0, 107, 'dibanishi', 'দিবানিশি', 'আবদুল্লাহ আল ইমরান', 1, 1, 1, '2020-09-20 17:35:29', '2021-05-26 22:13:45'),
(22, 'কালচক্র', NULL, '<p>শীতে তার কষ্ট হচ্ছে না ঠিক। কিন্তু অন্য এক কষ্ট শরীর কাঁপিয়ে ভেতরে ভেতরে নীল করে দিচ্ছে। ভয়ানক সেই কষ্টের নাম স্মৃতিকাতরতা। মানুষের জীবন হারানাের জীবন। জন্মের পর থেকেই হারানাের তালিকাটা তাই মৰ্ঘা হতে থাকে। শৈশব হারায়, কৈশাের হারায়, হারিয়ে যায় যৌবনও। হারায় বাবা-মা, স্বামী-স্ত্রী, কারাে কারাে সন্তান। একসময় চুল, দাত, এমনকি চোখের দৃষ্টিশক্তিও হারায় মানুষ। জীবন থেকে এভাবে একের পর এক সবকিছু হারাতে থাকলেও স্মৃতি হারায় না। বুকের গহিনে ঠিকই গুটিশুটি মেরে থেকে যায়।</p>\r\n\r\n<p>স্মৃতির প্রতি তীব্র কাতরতা থেকে তাই আমৃত্যু মুক্তি মেলে না মানুষের। আশালতা দেবীরও মেলেনি...&#39; জীবনের এমন অজস্র জটিল হিসাব-নিকাশের গল্প নিয়ে এ উপন্যাস। গল্প অসংখ্য হারিয়ে ফেলা অনুভূতিরও। কাহিনী যত এগােবে, তত উন্মােচিত হবে মৃত এক শিল্প অঞ্চলের বহুমাত্রিক মানুষের বৈচিত্র্যময় জীবনবােধ। &lsquo;কালচক্র পাটকলনির্ভর নদীঘেরা জনপদের ওপর রচিত এমন এক মানবিক আখ্যান, যেখানে জীবনের ভাঙা-গড়া, আনন্দ-বেদনার গল্পেরা চক্রাকারে বয়ে চলে অবিরাম।&nbsp;</p>', 'kalchokro', '18.jpg', 'kalchokro', '298926', 'in_stock', NULL, '350.00', '262.50', 0, '100', '5', NULL, NULL, 1, 1, NULL, 'a:2:{s:5:\"front\";a:1:{i:0;N;}s:4:\"back\";a:1:{i:0;N;}}', '7', NULL, NULL, 8, '9789849323624', '1st Published, 2018', '200', 19, 'বাংলা', 0, 107, 'kalchokro', 'কালচক্র', NULL, 1, 1, 1, '2020-09-20 17:35:29', '2021-06-12 05:22:34'),
