(785, 'ভাঙা ডানার প্রজাপতি', NULL, '<div class=\"kvgmc6g5 cxmmr5t8 oygrvhab hcukyx3x c1et5uql ii04i59q\" style=\"overflow-wrap: break-word; margin: 0px; white-space: pre-wrap; font-family: \">\r\n<div dir=\"auto\" style=\"font-family: inherit;\">ভাঙা ডানার প্রজাপতি বইয়ের মানুষগুলো হতে পারে ঘাপটি মেরে বসে থাকে একটা সাপ কিংবা পঁচা গলা মাংস খেতে চাওয়া একটা শকুন,হতে পারে ক্ষুদ্র একটা পিঁপড়েও, যাকে একটা তর্জনী চাইলেই পিষে ফেলতে পারে,অথবা খোড়া একটা প্রজাপতি, যার জীবন সাঙ্গ করে দেওয়া যেতে পারে টোকাটুকির মত তুচ্ছ সব খেলায়।কেউ কেউ তো মনের মধ্যে পুকুর নিয়েও ঘোরে,যেখানে শাপলা, পদ্ম, কলমির মত ভেসে থাকে কিছু মানুষ অথচ তারা নেই <span style=\"font-family:inherit\">হঠাৎ করে এই মানুষগুলোর যদি রূপান্তর ঘটে, হয়ে যায় তারা বরফ মানুষ তাহলে তাদের বুকের পাঁজরের হাড় দিয়ে তৈরী ব্যাক্তিগত বেহেশতের সিংহাসনে বসে থাকা নীতিনির্ধারকেরা পৃথিবীর গৌরবময় ইতিহাস লিখবে কাদের রক্ত ঝরিয়ে? ভালো মন্দের সমান্তরাল রেখায় মানুষের অবস্হান কোথায়?মরা শকুন,দোমাথার মানুষ, খোড়া প্রজাপতিদের জগতে নেই এর কোন উত্তর, আছে প্রশ্ন উদ্রেককারী আরও কিছু প্রশ্ন;এখানে নেই কোন আলো,নেই আলোর দিকে ছুটতে চাওয়া পথিকও।</span></div>\r\n</div>', NULL, 'vanga_danar_projapoti_1.png', 'vanga-danar-projapati', 'durbiin-514', 'in_stock', NULL, '200.00', '150.00', 0, '50', '5', NULL, NULL, 1, 1, NULL, 'a:2:{s:5:\"front\";a:1:{i:0;s:27:\"vanga_danar_projapoti_1.png\";}s:4:\"back\";a:1:{i:0;N;}}', '460', NULL, NULL, 58, NULL, '2021-03-27', NULL, 19, 'বাংলা', 0, 65, 'Farzana Yasmin', 'ফারজানা ইয়াসমিন', 'ভাঙা ডানার প্রজাপতি,Farzana Yasmin', 1, 1, 1, '2021-04-05 22:07:50', '2021-06-10 02:35:00'),
(786, 'ভালোবাসার রং নীল', NULL, '<div><span style=\"font-size:13px\">ভালোবাসা এক শাশ্বত অনুভূতি। ভালোবাসা কখনো হয় নরনারীর মাঝে। কখনো তা ফল্গুধারার মত বয়ে চলে মা আর সন্তানের মধ্যে। কখনো তা হয় রাজা আর তার সন্তানসম প্রজাদের মাঝে। আবার কখনো কখনো ভালোবাসা হয়ে ওঠে বিষাক্ত, দমবন্ধকরা। কখনো অতিপ্রাকৃত। যার ব্যাখ্যা দেয়া সম্ভব না।&nbsp;&nbsp;</span></div>\r\n\r\n<div><span style=\"font-size:13px\">ভালোবাসায় মুড়ে দেয়া এক গুচ্ছ ছোটগল্পের ঝুড়ি পাওয়া যাবে ভালোবাসার রং নীল-এ।&nbsp;&nbsp;</span></div>', NULL, 'tt.png', 'valobashar-rang-neel', 'durbiin-515', 'in_stock', NULL, '350.00', '262.50', 0, '50', '5', NULL, NULL, 1, 1, NULL, 'a:2:{s:5:\"front\";a:1:{i:0;s:6:\"tt.png\";}s:4:\"back\";a:1:{i:0;N;}}', '275', NULL, NULL, 158, '9789849511380', '2021-03-10', '192', 19, 'বাংলা', 0, 76, 'valobashar-rang-neel', 'Tabassum Naz - তাবাসসুম নাজ', 'ভালোবাসার রং নীল,তাবাসসুম নাজ', 1, 1, 1, '2021-04-05 22:44:00', '2021-06-14 14:19:27'),
(787, 'মেঘের আড়ালে রোদ', NULL, '<div><span style=\"font-size:13px\">শ্রাবণের মেঘগুলো আকাশে জমা হয়েছে। যে কোনো সময় ঝরে পড়তে পারে। ভিজিয়ে দিতে পারে পৃথিবীর মাঠ,&nbsp; ঘাট, সভ্য সমাজ সব কিছু। কিন্তু, সেদিকে কোন ভ্রূক্ষেপ নেই রুপমের। সে এক ধ্যানে তাকিয়ে আছে মৃন্ময়ীর দিকে। আকাশে যেমন মেঘ করেছে ঠিক তেমনি মেঘ জমেছে এখানেও। আকাশের মেঘে বৃষ্টি হলে চারপাশে অস্থিরতা নামে।&nbsp; এই মেঘে অস্থিরতা নেমেছে রুপমের মনে। বৃষ্টি হয়তো আসতে পারে।</span></div>\r\n\r\n<div><span style=\"font-size:13px\">&nbsp;চারপাশের মানুষের তাড়া দেখে বোঝা যাচ্ছে যে বৃষ্টির সম্ভাবনা খুবই প্রবল। রাস্তার পাশে ফুটপাতে যেসকল দোকানিরা বসেছে তারা তাদের সকল প্রস্তুতি&nbsp; ঝালিয়ে দেখে নিচ্ছে ঠিক আছে কি না? রাস্তার পথিকরা তাদের হাঁটার গতি বাড়িয়ে দিয়েছে। দিক না, তাতে রুপমের কি যায় আসে? সে আছে এক ধ্যানে মৃন্ময়ীর দিকে চেয়ে। যেন, কোন Molecule নিয়ে ল্যাব টেস্ট করার চেয়েও গুরুত্বপূর্ণ কাজে মত্ত সে। চোখ জোড়া যদিওবা ক্লান্তিতে ভরে উঠেছে, কিন্তু রুপমের মনে হচ্ছে এই চাঁদমুখখানি&nbsp; দেখার জন্য এভাবে এক জীবন শুধুমাত্র পাশে বসে কাটিয়ে দেওয়া যায়। মনে মনে বলছে সে-&nbsp;</span></div>', NULL, 'meghar-arala-rod.png', 'megher-arale-rodh', 'durbiin-516', 'in_stock', NULL, '225.00', '168.75', 0, '50', '5', NULL, NULL, 1, 1, NULL, 'a:2:{s:5:\"front\";a:1:{i:0;s:20:\"meghar-arala-rod.png\";}s:4:\"back\";a:1:{i:0;N;}}', '461', NULL, NULL, 41, '9789849547846', NULL, '103', 19, 'বাংলা', 0, 209, 'মেঘের আড়ালে রোদ', 'megher-arale-rod', 'মেঘের আড়ালে রোদ,নাহিদ নার্ম', 1, 1, 1, '2021-04-05 23:28:29', '2021-06-14 21:09:05'),
