(838, 'হঠাৎ ছায়ার ভুবনে', NULL, '<div class=\"kvgmc6g5 cxmmr5t8 oygrvhab hcukyx3x c1et5uql ii04i59q\" style=\"overflow-wrap: break-word; margin: 0px; white-space: pre-wrap; font-family: \">\r\n<div dir=\"auto\" style=\"font-family: inherit;\">চার বছরের সম্পর্ক রাসেল আর তনিমার। তাদের দুজনের সম্পর্কটা অল্প কিছুদিনের মধ্যে সুন্দর এক পরিণতি পাবে তনিমা এমনটাই জানতো। রাসেলের আহবানে সাড়া দিয়ে তনিমা যখন একমাত্র বন্ধু রাতুলকে নিয়ে বরিশালের উদ্দেশ্যে রওনা হলো, তখন তারা দুজনের কেউ-ই জানতো না, কী এক অজানা রহস্যময় ভুবনের দিকে তারা পা বাড়িয়েছে। তাদের ধারনায় ছিল না, সত্য মিথ্যার অদ্ভুত এক প্রহেলিকাময় জগৎ তাদের সামনে দু হাত মেলে দাঁড়িয়ে আছে। <span style=\"font-family:inherit\">একদিকে দীর্ঘদিনের প্রেমিক পুরুষের আহবান অপরদিকে নির্মল বন্ধুত্বের আশ্রয়, একদিকে তীব্র অবিশ্বাসের কাঁটা অপরদিকে নির্ভরতার আশ্বাস , একদিকে রহস্যময় ভুবনের ফাঁদ অপরদিকে মুক্ত জীবনের হাতছানি! তনিমা কী পারবে রাসেলের ছায়ায় গড়া ভুবনকে পেছনে ফেলে আলোর ভুবনে ফিরে আসতে? রাতুল কী শেষ পর্যন্ত সঙ্গে থাকবে তনিমার? রাসেল-ই বা কী এত সহজে আলোর ভুবনে আসতে দিবে তার দীর্ঘদিনের সাজানো অন্ধকার জগৎ? তনিমা, রাতুল, সর্বোপরি রাসেলের ছায়ার ভুবনে আপনারা সাদরে আমন্ত্রিত।</span></div>\r\n</div>', NULL, 'hothath-chayar-vubone.png', 'hothath-chayar-vubone', 'durbiin-561', 'in_stock', NULL, '250.00', '187.50', 0, '10', '2', NULL, NULL, 1, 1, NULL, '{\"front\":[null],\"back\":[null]}', '476', '', NULL, 58, NULL, '2021-03-22', '111', 19, 'বাংলা', 0, 37, 'হঠাৎ ছায়ার ভুবনে', 'hothath-chayar-vubone', 'তামান্না স্মৃতি', 1, 1, 1, '2021-04-18 18:56:57', '2021-06-14 13:31:52'),
(839, 'আঁধারের কাহন', NULL, '<p>আমেনা বেগম যা ভেবেছিলেন তাই রুহুল মিয়া নদীর পাড়ে বালির বস্তার উপর নিতান্তই বেখায়ালীভাবে বসে আছে। সূর্য ডুবেই গেছে ..তবে এখনো লালিমা কাটেনি আকাশে৷ চাঁদ উঠে গেছে , তবে আকাশে লালচে আলোর রেখাটা এখনও থাকায় দশ দিন বয়সী চাঁদকে কেমন যেন নিষ্প্রান লাগছে। আমেনা বেগম দ্রুত পা চালান। বালিতে তার পা আঁটকে যাচ্ছে বারবার। আঁচলে দুকেজি চাল গিট দিয়ে বাঁধা, সেই চালটুকু বুকের কাছে ধরে হাঁটছেন আমেনা বেগম৷&nbsp;<br />\r\nরুহুল মিয়ার কাছাকাছি গিয়ে হঠাৎ যেন কিছু একটায় ধাক্কা খেলেন আমেনা বেগম৷ ধাক্কা সামলাতে না পেড়ে ধপাস করে পড়ে গেলেন তিনি৷ নিজেকে সামলাবার চাইতে চালটুকু সামলাবার চিন্তা ছিলো বেশি , তাই চাল বাঁচলেও তিনি বেশ ব্যথা পেলেন৷ উঠে দাঁড়াবার শক্তি তার নেই ... মনে মনে নিজেকে গালাগালি করতে লাগলেন , ঘরের দিকে যাওয়াই উচিত ছিলো তার৷ এই আলো আঁধারের মধ্যে বালির মধ্যে দিয়ে আসা ঠিক হয়নি তার৷&nbsp;</p>\r\n\r\n<p>হঠাৎ তিন চারটা শিয়াল ডেকে উঠলো৷ এ অঞ্চলে আগে শিয়াল ছিলো না, ইদানীং কতগুলো আস্তানা গেড়েছে এখানে৷ সন্ধ্যার পর তাদের ডাকে বুকের ভেতরটা কেঁপে উঠে, এমনি ভয়ঙ্কর লাগে সেই ডাক! আমেনা বেগম আবার উঠে দাঁড়াবার চেষ্টা করলেন, পারলেন না৷&nbsp;ওখান থেকে গলায় যতটা শক্তি আনা যায় এনে জোরে ডাকলেন &quot;ও রানুর বাপ... উইঠা আসো ...&quot; সে ডাক রুহুল মিয়ার কানে পৌছালো কি না কে জানে ! তবে তাকে উঠতে সাহায্য করার জন্য একটা হাত ঠিকই এলো ... নরম কোমল একটা মেয়ের হাত !&nbsp;</p>', NULL, 'adharer-kahon.png', 'adharer-kahon', 'durbiin-562', 'in_stock', NULL, '252.00', '189.00', 0, '100', '5', NULL, NULL, 1, 1, NULL, 'a:2:{s:5:\"front\";a:1:{i:0;N;}s:4:\"back\";a:1:{i:0;N;}}', '477', NULL, NULL, 58, NULL, '2021-03-23', '96', 19, 'বাংলা', 0, 78, 'adharer-kahon', 'তামান্না জেনিফার', 'আঁধারের কাহন', 1, 1, 1, '2021-04-18 22:47:36', '2021-06-14 22:51:39'),
