(883, 'কর্পোরেট হিরো', NULL, '<p>ব্যবসায় শিক্ষা বিভাগের ছাত্রী হিসেবে আমার সবসময় স্বপ্ন জাগে একদিন কর্পোরেটে যাব, হিরো হব। কিন্তু হিরো হতে চাইলে শিখা লাগবে অনেক কিছু। ইংরেজিতে লিখা বইগুলো পড়ে নিজের ভাষায় নিজের দেশের কোম্পানির উদাহরণ দিয়ে এই বইটিা লেখা হয়েছে। মার্কেটিং এর বেসিক কিছু দিক নিয়ে আলোচনা করাই আমার মূল উদ্দেশ্য ছিল। আশাকরি এই বইটি আপনাকে মার্কেটিং এর বেসিক কিছু ধারণা দিবে।&nbsp;</p>', NULL, 'corporate-hero.jpg', 'corporate-hero', 'durbiin-008834', 'in_stock', NULL, '200.00', '150.00', 0, '50', '3', NULL, NULL, 1, 1, NULL, 'a:2:{s:5:\"front\";a:1:{i:0;N;}s:4:\"back\";a:1:{i:0;N;}}', '494', NULL, NULL, 8, '9789849522355', '1st Published, 2021', '96', 19, 'বাংলা', 0, 16, 'কর্পোরেট হিরো', NULL, 'তমা রশিদ', 1, 1, 1, '2021-04-25 04:13:06', '2021-05-17 00:05:50'),
