(971, 'অযাচিত ঘণ্টাধ্বনি (হার্ডকভার)', NULL, '<p>ঘণ্টাধ্বনি যেমনভাবে মনকে সচকিত ও সজাগ করে তোলে তেমনিভাবেই কবিতাগুলোর রূপ ও অবয়ব পাঠকের মনোযোগকে কেন্দ্রীভূত করে নিয়ে চলে নিজের দিকে। কবিতাগুলোতে সমসাময়িক চিন্তার অন্তর্দ্বন্দ্বের মানসিক ও সামাজিক প্রেক্ষাপট বিশ্লেষণে কবির গভীর অন্তদৃর্ষ্টির পরিচয় পাওয়া যায়। সহজ&mdash;সরল রূপকে আমাদের মাঝে বর্তমান কিছু প্রশ্ন উত্থাপিত হয়েছে কবিতায়। প্রেমের সাবলীল প্রকাশ ও বিরহ&mdash;বিষাদে ভিজে আছে কবিতাগুলো।&nbsp;</p>', NULL, 'ojachito_ghonta_dhoni.jpg', 'ajachito-ghontadhoni', 'durbiin-00971', 'in_stock', NULL, '150.00', '120.00', 0, '5', '1', NULL, NULL, 1, 1, NULL, '{\"front\":[null],\"back\":[null]}', '540', '', NULL, 133, NULL, '1st Published, 2021', NULL, NULL, 'বাংলা', 0, 4, 'অযাচিত ঘণ্টাধ্বনি - তুষার মাহমুদ', 'ajachito ghontadhoni', 'অযাচিত ঘণ্টাধ্বনি - তুষার মাহমুদ', 1, NULL, 1, '2021-06-02 12:41:11', '2021-06-04 04:19:27'),
(972, 'শেষ যাত্রা', NULL, '<p>যেকোনাে সৃষ্টির একমাত্র লক্ষ্যই থাকে ধ্বংস হওয়া। মানুষও আজন্ম পথ চলে একটা সফল, সুন্দর আর অর্থবহ মৃত্যুর উদ্দেশ্যে। একটা জীবনের আর সবকিছু মিথ্যা হতে পারে, সব ধরনের স্বপ্ন অবাস্তব হতে পারে, যে কোনাে সম্ভাবনা অলীক হতে পারে। কিন্তু প্রতিটি জন্মের সাথে জুড়ে দেওয়া একমাত্র সত্য মৃত্যুর হিমশীতলতা কেউ অস্বীকার করতে পারেনা। একজন মানুষের জীবনযাপনের উদ্দেশ্যই থাকে যে, এই মৃত্যুমুখে শেষযাত্রাটা কতটা সফল হবে।<br />\r\nকোনাে মৃত্যু একজন মানুষকে মহিমান্বিত করে, কোনাে মৃত্যু করে ঘুণিত, কোনাে মৃত্যু বাকী মানব জাতিকে করে দেয় বাকরূদ্ধ, কোনও মৃত্যুতে মানুষ স্বস্তির নিঃশ্বাসও ফেলে। আবার সহজ স্বাভাবিক মৃত্যুও কখনাে কখনাে মানুষকে ভাবায়।</p>\r\n\r\n<p><br />\r\nএধরণেরই পাঁচটি মৃত্যুর অভিমুখে জীবন নিয়ে যাত্রার বর্ণনা দিয়েছেন মিম্ মি রহমান তার শেষ যাত্রা গল্প সংকলনটিতে। এটি মিম্ মির পঞ্চম একক গল্পসংকলন, যেখানে ছয়টি গল্পের ভিন্ন ভিন্ন আঙ্গিকের বর্ণনা পাঠককে ভাবনার খােরাক জোগাবে। মিম্ মি ইতােমধ্যে সাইকোলজিক্যাল গল্প লিখে ব্যাপক পাঠকনন্দিত। ব্যক্তিজীবনে কঠিন আর অসহনীয় অভিজ্ঞতার পথ পাড়ি দিয়ে সফলতায় দাঁড়ানাে একজন নারী বুদ্ধিমত্তা, চিন্তাশীলতা ও বাস্তবজ্ঞানে সমৃদ্ধ হবে, এটাই পরম স্বাভাবিক। আর মিম মির লেখায় সবসময় এই পরিমিতিবােধ আর পরিনতবােদ্ধার পরিচয় পাওয়া যায়। ফ্ল্যাপ লিখতে গিয়ে খুঁটিয়ে খুঁটিয়ে পড়েও আমার পাঠক মন একটু অন্যরকম ভাবনায় আক্রান্ত হয়েছে, তৃপ্ত হয়েছে। আমার ঐকান্তিক শুভকামনা, বইটি প্রকৃত পাঠকদের সমাদর অবশ্যই পাবে।</p>', NULL, 'sesh_jatra.jpg', 'shesh-jatra', 'durbiin-00972', 'in_stock', NULL, '200.00', '150.00', 0, '5', '1', NULL, NULL, 1, 1, NULL, '{\"front\":[null],\"back\":[null]}', '541', '', NULL, 133, '9789849545019', '1st Published, 2021', '64', NULL, 'বাংলা', 0, 11, 'শেষ যাত্রা - মিম্ মি রহমান', 'shesh jatra', 'শেষ যাত্রা - মিম্ মি রহমান', 1, 1, 1, '2021-06-02 12:49:10', '2021-06-08 13:43:33'),
