(989, 'খোলস', NULL, '<p><span style=\"color:rgb(34, 34, 34); font-family:consolas,lucida console,courier new,monospace; font-size:12px\">জয়ন্ত শাহরিয়ার, শখের গোয়েন্দা। সারাদিনের আড্ডা এক চায়ের দোকানে। কিন্তু আজকে তার নজর পড়েছে একজন সাধারণ পোশাকের লোকের উপর। জয়ন্ত নিশ্চিত এই লোক যা দেখাচ্ছে তা আসলে সে না। অগত্যা পিছু নিলো সে। একে একে ঘটনায় চলে আসে শীর্ষ ধনী ইফতেখার আনিস। বন্দি হয় জয়ন্ত। এক গোপন সংগঠন এর বিছানো ফঁাদে পা দিয়েছে সে। পালিয়েও শান্তি নেই। ঘুড়ির নাটাই যে এখনো অন্যের হাতেই। এই এক অদ্ভুত খেলা। যে খেলার খেলোয়ার আর দর্শক একই মানুষ। এ এক ষড়যন্ত্রের খেলা। এ এক খোলস বদলের খেলা। জয়ন্ত কি পারবে এই খোলস বদলের খেলায় পেড়ে উঠতে নাকি পা দিবে প্রতিপক্ষের নতুন কোনো চালে। </span></p>', NULL, 'final_cover.jpg', 'kholosh', 'durbiin-00989', 'in_stock', NULL, '250.00', '188.00', 0, '5', '1', NULL, NULL, 1, 1, NULL, '{\"front\":[null],\"back\":[null]}', '554', '', NULL, 179, NULL, '1st Published, 2021', '160', NULL, 'বাংলা', 0, 19, 'খোলস - শুভাশীষ বাপ্পী', 'kholosh', NULL, 1, NULL, 1, '2021-06-06 11:41:11', '2021-06-14 18:12:43'),
(990, 'তারাশঙ্কর অলৌকিক গল্প সমগ্র', NULL, '<p><span style=\"color:rgb(34, 34, 34); font-family:consolas,lucida console,courier new,monospace; font-size:12px\">তারাশঙ্কর বন্দোপাধ্যায় বাংলা সাহিত্যের এক অনন্য নাম। কি উপন্যাস আর কি গল্প সব কিছুতেই তার বিচরণ ছিলো একই তালে। সামাজিক বিভিন্ন সমস্যা তার লেখায় উঠে আসলেও আমরা হয়ত অনেকেই জানি না তার লেখা অলৌকিক গল্প আছে বেশ কিছু৷ হুমায়ুন আহমেদ এর কল্যাণে হয়ত ভুলোর ছলনা আমাদের পড়া হয়েছে অনেকেরই কিন্তু তার অন্য অলৌকিক ঘরণার লেখাগুলো নিয়ে আমরা কতটুকুই বা জানিম তাই জানতে এবং জানাতে আমরা আপনাদের সামনে নিয়ে আসছি তারাশঙ্কর এর প্রথম অলৌকিক গল্প সমগ্র৷ আমাদের চিরকালীন সাহিত্যের প্রথম নিবেদন তাহলে তারাশঙ্কর অলৌকিক গল্প সমগ্র দিয়েই শুরু হলো। </span></p>', NULL, '190621519_213697780591773_4101776367691500974_n__281_29.jpg', 'tarashonkor-oloukik-golpo-somogro', 'durbiin-00990', 'in_stock', NULL, '350.00', '263.00', 0, '4', '1', NULL, NULL, 1, 1, NULL, '{\"front\":[null],\"back\":[null]}', '555', '', NULL, 179, NULL, '1st Published, 2021', '220', NULL, 'বাংলা', 0, 101, 'তারাশঙ্কর অলৌকিক গল্প সমগ্র -  তারাশঙ্কর বন্দ্যোপাধ্যায়', 'tarashonkor oloukik golpo somogro', NULL, 1, 1, 1, '2021-06-06 11:53:10', '2021-06-14 16:06:32');
