(994, 'রুইতনের বিবি', NULL, '<p><span style=\"color:rgb(34, 34, 34); font-family:consolas,lucida console,courier new,monospace; font-size:12px\">ইথার আখতারুজ্জানের দ্বিতীয় কাব্যগ্রন্থ &quot;রুইতনের বিবি&quot;। প্রেম, দ্রোহ আর জীবনের কবিতার সংকলন &quot; রুইতনের বিবি&quot;। কবি স্বপ্ন দেখেন একদিন এ শহর কবিতার হবে। সেই শহরের ভালোবাসার বৃষ্টিতে আমন্ত্রণ সবার। </span></p>', NULL, 'ruton_r_bibi.jpg', 'ruitoner-bibi', 'durbiin-00994', 'in_stock', NULL, '200.00', '150.00', 0, '4', '1', NULL, NULL, 1, 1, NULL, '{\"front\":[null],\"back\":[null]}', '558', '', NULL, 180, '9789849554455', '1st Published, 2021', '64', NULL, 'বাংলা', 0, 280, 'রুইতনের বিবি - ইথার আখতারুজ্জামান', 'ruitoner bibi', NULL, 1, NULL, 1, '2021-06-07 08:12:48', '2021-06-15 01:21:46'),
(995, '১০০ গ্রেট মার্কেটিং আইডিয়া', NULL, '<p><span style=\"color:rgb(34, 34, 34); font-family:consolas,lucida console,courier new,monospace; font-size:12px\">অনলাইন/অফলাইন মিলিয়ে দেশে এখন ব্যবসায়ী ও উদ্যোক্তার সংখ্যা নেহায়েত কম না। কিন্তু অনেকেরই মার্কেটিং নিয়ে জানাশোনা কম। প্রমোশন বলতে বুঝি, স্রেফ ফেসবুক বুস্টিং। কিন্তু ব্যবসা কেবল প্রোডাক্ট বা সার্ভিস দিয়ে হয় না। দরকার উপযুক্ত ব্র&zwj;্যান্ডিং এবং মার্কেটিং। এই বইয়ের লেখক ইউনিভার্সিটি অফ ওয়েস্টমিনিস্টারের মার্কেটিং বিভাগের প্রফেসর হলেও এই বইতে তিনি কোনো জটিল তত্ত্ব নিয়ে আসেননি। বরং সহজ ও মজা করে বলে গেছেন ১০০ টি চমৎকার মার্কেটিং আইডিয়া। ইন্টারেস্টিং ব্যাপার হলো, এগুলো কোনো থিয়োরিটিক্যাল কেতাবি আইডিয়া না। প্রতিটা আইডিয়াই তিনি নিয়েছেন বিশ্বের নামকরা বিভিন্ন ব্র&zwj;্যান্ডের স্ট্র&zwj;্যাটেজি বা ক্যাম্পেইন থেকে। </span></p>\r\n\r\n<p><span style=\"color:rgb(34, 34, 34); font-family:consolas,lucida console,courier new,monospace; font-size:12px\">এ তালিকায় আছে, বিএমডব্লিউ, ডিএইচএল, র&zwj;্যাডিসন, রেমিংটন, ভক্সওয়াগন&#39;র মতো জায়ান্ট প্রতিষ্ঠানও। বইয়ের প্রতিটা আইডিয়ার ক্ষেত্রে প্রথমে তিনি আইডিয়ার ব্যাপারে ধারণা দিয়েছেন। এরপর কোন ঘটনা বা ক্যাম্পেইন থেকে তিনি আইডিয়া নিয়েছেন, সে গল্প বলেছেন। সব শেষে, আইডিয়ার প্রয়োগ কীভাবে করবেন, কী কী মাথায় রাখতে হবে সেসব বুলেট পয়েন্ট আকারে তুলে দিয়েছেন। মার্কেটিং নিয়ে এতো মজাদার বই বাংলায় বোধহয় আর নেই। এতোটাই সহজ করে লেখা একজন মুদি দোকানিও চাইলে আইডিয়াগুলো এপ্লাই করতে পারবে। প্রতিটা ক্ষুদ্র ও মাঝারি ব্যবসায়ীর জন্য বইটা গাইডবুক হিসেবে কাজ করবে। </span></p>', NULL, '100-great-marketing-ideas.jpg', '100-great-marketing-ideas', 'durbiin-00995', 'in_stock', NULL, '300.00', '225.00', 0, '10', '1', NULL, NULL, 1, 1, NULL, '{\"front\":[null],\"back\":[null]}', '559', '', NULL, 181, '9789849415312', '1st Published, 2020', NULL, NULL, 'বাংলা', 0, 35, '১০০ গ্রেট মার্কেটিং আইডিয়া -  জিম ব্লাইথ', '100-great marketing ideas', NULL, 1, 1, 1, '2021-06-07 10:20:20', '2021-06-12 03:53:41'),
(996, 'অ্যাভালাঞ্চ', NULL, '<p><span style=\"color:rgb(34, 34, 34); font-family:consolas,lucida console,courier new,monospace; font-size:12px\">ছুটি কাটাতে এসে ঝামেলায় পড়ে গেল মনরো দম্পতি। হোটেল থেকে গায়েব প্রফেসর রবার্ট মনরো&rsquo;র স্ত্রী। কোনো পূর্বাভাস নেই, নেই অন্তর্ধানের কোনো কারণ। মহিলাকে কি কিডন্যাপ করা হয়েছে, নাকি নিজেই ভেগেছে আগের রাতে পার্টি করা সুদর্শন ইটালিয়ানের সাথে?</span></p>\r\n\r\n<p><span style=\"color:rgb(34, 34, 34); font-family:consolas,lucida console,courier new,monospace; font-size:12px\">একের পর এক ঘটনা-দুর্ঘটনার শিকার প্রফেসর অবশেষে বুঝতে পারল, ফেঁসে গেছে আন্তর্জাতিক চক্রান্তের জালে। বেশ কয়েক পক্ষের ইঁদুর-বেড়াল খেলার গুটিতে পরিণত হয়েছে সে। </span><span style=\"color:rgb(34, 34, 34); font-family:consolas,lucida console,courier new,monospace; font-size:12px\">পর্দার আড়ালে কলকাঠি নাড়ছে কে বা কারা? জেমস প্যাটারসনের দুর্দান্ত গতির বুকশট অ্যাভালাঞ্চ -এ মিলবে উত্তর। </span></p>\r\n\r\n<p><span style=\"color:rgb(34, 34, 34); font-family:consolas,lucida console,courier new,monospace; font-size:12px\">একের পর এক ঘটনা-দুর্ঘটনার শিকার প্রফেসর অবশেষে বুঝতে পারল, ফেঁসে গেছে আন্তর্জাতিক চক্রান্তের জালে। বেশ কয়েক পক্ষের ইঁদুর-বেড়াল খেলার গুটিতে পরিণত হয়েছে সে।</span></p>\r\n\r\n<p>&nbsp;</p>', NULL, 'avalanche.jpg', 'avalanche', 'durbiin-00996', 'in_stock', NULL, '180.00', '135.00', 0, '5', '1', NULL, NULL, 1, 1, NULL, '{\"front\":[null],\"back\":[null]}', '560', '', NULL, 181, '9789849415305', '1st Published, 2020', '112', NULL, 'বাংলা', 0, 10, 'অ্যাভালাঞ্চ - জেমস প্যাটারসন', 'avalanche', NULL, 1, 1, 1, '2021-06-07 10:29:55', '2021-06-10 00:06:00'),
(997, 'আল্লাহ’স মাউন্টেন', 'জারদের সময়, রাশিয়া-চেচনিয়া যুদ্ধ, ইমাম শামিলের দীর্ঘ ত্রিশ বছরের সংগ্রাম, সোভিয়েত ইউনিয়নের স্বরুপ, আধুনিক রাশিয়ার রাজনীতি', '<p>চেচেনরা বরাবরই স্বাধীনচেতা জাতি। রাশিয়ার সাথে তাদের বিরোধ শুরু হয় প্রায় আড়াইশো বছর আগে। ১৯৯৫ সালের যুদ্ধে এএফপি&#39;র সিনিয়র করসপন্ডেন্ট সেবাস্টিয়ান স্মিথ পুরো যুদ্ধটাই কাভার করেন। ঘুরে বেড়িয়েছেন যুদ্ধবিধ্বস্ত চেচনিয়ার এ প্রান্ত থেকে ও প্রান্ত, সাক্ষাতকার নিয়েছেন অসংখ্য যুদ্ধপীড়িত মানুষ, গেরিলা ও রাশিয়ান সৈনিকদের। একসাথে বসে চা খেয়েছেন স্বয়ং যোখার দুদায়েভ, শামিল বাসায়েভের মতো কীংবদন্তি চেচেন গেরিলা নেতাদের। যুদ্ধের ভয়াবহ ও শ্বাসরুদ্ধকর বর্ণনা ছাড়াও এই বইয়ের লক্ষাধিক শব্দের বুননে উঠে এসেছে ককেশাস অঞ্চলের কয়েক&#39;শ বছরের ইতিহাস, সভ্যতা, সংস্কৃতি, জারদের সময় থেকে শত বছর ধরে চলে আসা রাশিয়া-চেচনিয়া যুদ্ধ, ইমাম শামিলের দীর্ঘ ত্রিশ বছরের সংগ্রাম থেকে শুরু করে সোভিয়েত ইউনিয়নের স্বরুপ, আধুনিক রাশিয়ার রাজনীতি, এসপিওনাজ, প্রোপাগাণ্ডা, তেলের বাজার নিয়ে পরাশক্তিগুলোর দ্বন্দ্ব, ভ্লাদিমির পুতিনের উত্থান- কোনো কিছুই বাদ রাখেননি সেবাস্টিয়ান স্মিথ। রিয়েল লাইফ থ্রিলারের রুদ্ধশ্বাস অভিযানে আপনাকে স্বাগতম।&nbsp;</p>', NULL, 'allah_s-mountain.jpg', 'allah-s-mountain', 'durbiin-00997', 'in_stock', NULL, '450.00', '338.00', 0, '5', '1', NULL, NULL, 1, 1, NULL, '{\"front\":[null],\"back\":[null]}', '561', '', NULL, 181, NULL, '1st Published, 2019', '384', NULL, 'বাংলা', 0, 17, 'আল্লাহ’স মাউন্টেন -  সেবাস্টিয়ান স্মিথ', 'allah s mountain', NULL, 1, NULL, 1, '2021-06-07 10:43:06', '2021-06-10 07:09:47'),
